import { Add, Remove } from "@material-ui/icons";
import styled from "styled-components"
import Announcement from "../components/Announcement";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";

const Container = styled.div``;

const Wrapper = styled.div`
    padding: 20px;
`;

const Title = styled.h1`
    font-weight: 300;
    text-align: center;
`;

const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
`;

const TopButton = styled.button`
    padding: 10px;
    font-weight: 600;
    cursor: pointer;
    border: ${props=>props.type === "filled" && "none"};
    background-color: ${props=>props.type === "filled" ? "black" : "transparent"};
    color: ${props=>props.type === "filled" && "white"};
`;

const TopTexts = styled.div``;

const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    margin: 0px 15px;
`;

const Bottom = styled.div`
    display: flex;
    justify-content: space-between;
`;

const Info = styled.div`
    flex: 3;
`;

const Product = styled.div`
    display: flex;
    justify-content: space-between;
`;

const ProductDetail = styled.div`
    flex: 2;
    display: flex;
`;

const Image = styled.img`
    width: 200px;
`;

const Details = styled.div`
    padding: 20px;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductID = styled.span``;

const ProductColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: ${(props) => props.color};
`;

const ProductSize = styled.span``;

const PriceDetail = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const ProductAmountContainer = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px;
`;

const ProductAmount = styled.div`
    font-size: 24px;
    margin: 5px;
`;

const ProductPrice = styled.div`
    font-size: 28px;
    font-weight: 200;
`;

const Hr = styled.hr`
    background-color: #eee;
    border: none;
    height: 1px;
`;

const Summary = styled.div`
    flex: 1;
    border: 0.5px solid lightgray;
    border-radius: 10px;
    padding: 20px;
    height: 50vh;
`;

const SummaryTitle = styled.h1`
    font-width: 200;
`;

const SummaryItem = styled.div``;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const Button = styled.button``;

const Cart = () => {
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
          <Title>YOUR BAG</Title>
          <Top>
              <TopButton>CONTINUE SHOPPING</TopButton>
              <TopTexts>
                  <TopText>Shopping Bag(2)</TopText>
                  <TopText>Your Wishlist(0)</TopText>
              </TopTexts>
              <TopButton type="filled">CHECKOUT NOW</TopButton>
          </Top>
          <Bottom>
              <Info>
                  <Product>
                      <ProductDetail>
                          <Image src="https://img.ltwebstatic.com/images3_pi/2020/12/31/16094028904e91d88fe08938e42002cf848b3949fd_thumbnail_900x.webp"/>
                          <Details>
                              <ProductName><b>Product:</b> Triangle Tie Side Bikini Swimsuit</ProductName>
                              <ProductID><b>ID:</b> 3790990393</ProductID>
                              <ProductColor color="black"/>
                              <ProductSize><b>Size:</b> M </ProductSize>
                          </Details>
                      </ProductDetail>
                      <PriceDetail>
                          <ProductAmountContainer>
                            <Add/>
                            <ProductAmount>2</ProductAmount>
                            <Remove/>
                          </ProductAmountContainer>
                          <ProductPrice>$39.95</ProductPrice>
                      </PriceDetail>
                  </Product>
                <Hr/>
                  <Product>
                      <ProductDetail>
                          <Image src="https://img.ltwebstatic.com/images3_pi/2020/12/30/1609303088bde0fa63889cd7a0c0d7e99362d37eb1_thumbnail_900x.webp"/>
                          <Details>
                              <ProductName><b>Product:</b> Cut Out Tie Back Ruffle Trim Wrap Hem Slip Dress</ProductName>
                              <ProductID><b>ID:</b> 3790993949</ProductID>
                              <ProductColor color="#6E98D4"/>
                              <ProductSize><b>Size:</b> M 
                              </ProductSize>
                          </Details>
                      </ProductDetail>
                      <PriceDetail>
                          <ProductAmountContainer>
                            <Add/>
                            <ProductAmount>1</ProductAmount>
                            <Remove/>
                          </ProductAmountContainer>
                          <ProductPrice>$49.95</ProductPrice>
                      </PriceDetail>
                  </Product>
              </Info>
              <Summary>
                  <SummaryTitle>ORDER SUMMARY</SummaryTitle>
                  <SummaryItem>
                      <SummaryItemText>Subtotal</SummaryItemText>
                      <SummaryItemPrice>$ 129.85</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                      <SummaryItemText>Estimated Shipping</SummaryItemText>
                      <SummaryItemPrice>$ 8.90</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                      <SummaryItemText>Shipping Discount</SummaryItemText>
                      <SummaryItemPrice>$ -8.90</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                      <SummaryItemText type="total">Total</SummaryItemText>
                      <SummaryItemPrice>$ 129.85</SummaryItemPrice>
                  </SummaryItem>
                  <Button>CHECKOUT NOW</Button>
              </Summary>
          </Bottom>
      </Wrapper>
      <Footer />
    </Container>
  )
}

export default Cart
