import styled from "styled-components"
import Navbar from "../components/Navbar";
import Announcement from "../components/Announcement";
import Newsletter from "../components/Newsletter";
import Footer from "../components/Footer";
import { Add, Remove } from "@material-ui/icons";

const Container = styled.div``;

const Wrapper = styled.div`
    padding: 50px;
    display: flex;
`;
const ImgContainer = styled.div`
    flex: 1;
`;

const Image = styled.img`
    width: 100%;
    height: 95%;
    object-fit: cover;
`;

const InfoContainer = styled.div`
    flex: 1;
    padding: 0px 50px;
`;

const Title = styled.h1`
    font-weight: 200;
`;

const Desc = styled.p`
    margin: 20px 0px;
`;

const Price = styled.span`
    font-weight: 100;
    font-size: 30px;
`;

const FilterContainer = styled.div`
    width: 50%;
    margin: 30px 0px;
    display: flex;
    justify-content: space-between;
`;

const Filter = styled.div`
    display: flex;
    align-items: center;
`;

const FilterTitle = styled.span`
    font-size: 20px;
    font-weight: 200;
`;

const FilterColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: ${props=>props.color};
    margin: 0px 5px;
    cursor: pointer;
`;

const FilterSize = styled.select`
    margin: 10px;
    padding: 5px;
`;

const FilterSizeOption = styled.option``;

const AddContainer = styled.div`
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: space-between;
`;

const AmountContainer = styled.div`
    display: flex;
    align-items: center;
    font-weight: 700;
`;

const Amount = styled.span`
    width: 30px;
    height: 30px;
    border-readius: 10px;
    border: 1px solid teal;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0px 10px;
`;

const Button = styled.button`
    padding: 15px;
    border: 1.5px solid teal;
    backgroung-color: white;
    cursor: pointer;
    font-weight: 500;

    &:hover{
        background-color: #f8f4f4;
    }
`;

const Product = () => {
  return (
    <Container>
       <Navbar/>
       <Announcement/>
       <Wrapper>
           <ImgContainer>
            <Image src="https://cdn.shopify.com/s/files/1/0092/1842/4917/products/1_ded640ce-47c6-4c4f-b525-f34cbf9cc0e6_600x.jpg" />
           </ImgContainer>
           <InfoContainer>
               <Title>Marsala Twist High Waisted Bikini</Title>
               <Desc>
               One of our best-selling swimsuits! This Marsala Twist High-Waist Bikini detailed with a twist at the front and adjustable straps for the perfect fit. The high-waisted bottom provides flattering tummy coverage.</Desc>
               <Price>$ 38.95</Price>
               <FilterContainer>
                   <Filter>
                       <FilterTitle>Color</FilterTitle>
                       <FilterColor color="Darkred" />
                       <FilterColor color="Black" />
                       <FilterColor color="Darkblue" />
                   </Filter>
                   <Filter>
                       <FilterTitle>Size</FilterTitle>
                       <FilterSize>
                           <FilterSizeOption>XS</FilterSizeOption>
                           <FilterSizeOption>S</FilterSizeOption>
                           <FilterSizeOption>M</FilterSizeOption>
                           <FilterSizeOption>L</FilterSizeOption>
                           <FilterSizeOption>XL</FilterSizeOption>
                       </FilterSize>
                   </Filter>
               </FilterContainer>
               <AddContainer>
                   <AmountContainer>
                       <Remove/>
                       <Amount>1</Amount>
                       <Add/>
                   </AmountContainer>
                   <Button>ADD TO CART</Button>
               </AddContainer>
           </InfoContainer>
       </Wrapper>
       <Newsletter/>
       <Footer/>
    </Container>
  )
}

export default Product
